const isInvalidId = (n: number): boolean => {
  const s = String(n);
  if (s.length % 2 !== 0) return false;
  const half = s.length / 2;
  return s.slice(0, half) === s.slice(half);
};

const findInvalidIdsInRange = (start: number, end: number): number[] => {
  const invalidIds: number[] = [];
  const startStr = String(start);
  const endStr = String(end);
  const minLen = startStr.length;
  const maxLen = endStr.length;

  for (let len = minLen; len <= maxLen; len++) {
    if (len % 2 !== 0) continue;
    const halfLen = len / 2;
    const minHalf = len === minLen ? Math.ceil(start / Math.pow(10, halfLen)) : Math.pow(10, halfLen - 1);
    const maxHalf = len === maxLen ? Math.floor(end / Math.pow(10, halfLen)) : Math.pow(10, halfLen) - 1;

    for (let half = minHalf; half <= maxHalf; half++) {
      const candidate = Number(String(half) + String(half));
      if (candidate >= start && candidate <= end) {
        invalidIds.push(candidate);
      }
    }
  }
  return invalidIds;
};

export const solve = (input: string) => {
  const ranges = input.trim().split(",").map((range) => {
    const [start, end] = range.split("-").map(Number);
    return { start, end };
  });

  let sum = 0;
  for (const { start, end } of ranges) {
    const invalidIds = findInvalidIdsInRange(start, end);
    sum += invalidIds.reduce((a, b) => a + b, 0);
  }
  return sum;
};

console.log(solve(input));

