import { lines } from "../../../lib/utils";

const input = await Bun.file(import.meta.dir + "/data/input.txt").text();

const parseProblems = (input: string) => {
  const rows = lines(input).filter((r) => r.length > 0);
  if (rows.length === 0) return [];

  const maxLen = Math.max(...rows.map((r) => r.length));
  const grid = rows.map((r) => r.padEnd(maxLen, " "));

  const operatorRow = grid[grid.length - 1];
  const numberRows = grid.slice(0, -1);

  const problems: { numbers: bigint[]; operator: string }[] = [];
  let currentProblem: { cols: number[]; operator: string } | null = null;

  for (let col = 0; col < maxLen; col++) {
    const isSpace = numberRows.every((row) => row[col] === " ");
    const opChar = operatorRow[col];

    if (isSpace && (opChar === " " || opChar === undefined)) {
      if (currentProblem !== null) {
        const numbers = numberRows
          .map((row) => {
            const str = currentProblem!.cols.map((c) => row[c]).join("");
            const num = parseInt(str.trim(), 10);
            return isNaN(num) ? null : BigInt(num);
          })
          .filter((n): n is bigint => n !== null);
        problems.push({ numbers, operator: currentProblem.operator });
        currentProblem = null;
      }
    } else {
      if (currentProblem === null) {
        currentProblem = { cols: [col], operator: opChar };
      } else {
        currentProblem.cols.push(col);
        if (opChar === "+" || opChar === "*") {
          currentProblem.operator = opChar;
        }
      }
    }
  }

  if (currentProblem !== null) {
    const numbers = numberRows
      .map((row) => {
        const str = currentProblem!.cols.map((c) => row[c]).join("");
        const num = parseInt(str.trim(), 10);
        return isNaN(num) ? null : BigInt(num);
      })
      .filter((n): n is bigint => n !== null);
    problems.push({ numbers, operator: currentProblem.operator });
  }

  return problems;
};

export const solve = (input: string) => {
  const problems = parseProblems(input);

  let total = 0n;
  for (const p of problems) {
    if (p.numbers.length === 0) continue;
    const result =
      p.operator === "+"
        ? p.numbers.reduce((a, b) => a + b, 0n)
        : p.numbers.reduce((a, b) => a * b, 1n);
    total += result;
  }

  return total;
};

console.log(solve(input).toString());

