    const isSpaceCol = numberRows.every((row) => row[col] === " ");
    const opChar = operatorRow[col] ?? " ";

    if (isSpaceCol && opChar === " ") {
      finalizeProblem();
    } else if (currentProblem === null) {
      currentProblem = { cols: [col], operator: opChar };
    } else {
      currentProblem.cols.push(col);
      if (opChar === "+" || opChar === "*") {
