import { lines } from "../../../lib/utils";

const input = await Bun.file(import.meta.dir + "/data/input.txt").text();

type Point = [number, number, number];

const parsePoints = (input: string): Point[] =>
  lines(input).map((line) => {
    const [x, y, z] = line.split(",").map(Number) as Point;
    return [x, y, z];
  });

const distanceSquared = (a: Point, b: Point): number =>
  (a[0] - b[0]) ** 2 + (a[1] - b[1]) ** 2 + (a[2] - b[2]) ** 2;

const createUnionFind = (n: number) => {
  const parent: number[] = Array.from({ length: n }, (_, i) => i);
  const rank: number[] = Array(n).fill(0);

  const find = (x: number): number => {
    if (parent[x] !== x) parent[x] = find(parent[x]);
    return parent[x];
  };

  const union = (x: number, y: number): void => {
    const px = find(x);
    const py = find(y);
    if (px === py) return;
    if (rank[px] < rank[py]) {
      parent[px] = py;
    } else if (rank[px] > rank[py]) {
      parent[py] = px;
    } else {
      parent[py] = px;
      rank[px]++;
    }
  };

  const getCircuitSizes = (): number[] => {
    const sizes = new Map<number, number>();
    for (let i = 0; i < n; i++) {
      const root = find(i);
      sizes.set(root, (sizes.get(root) ?? 0) + 1);
    }
    return [...sizes.values()].sort((a, b) => b - a);
  };

  return { find, union, getCircuitSizes };
};

export const solve = (input: string, connections = 1000) => {
  const points = parsePoints(input);
  const n = points.length;

  const pairs: { dist: number; i: number; j: number }[] = [];
  for (let i = 0; i < n; i++) {
    for (let j = i + 1; j < n; j++) {
      pairs.push({ dist: distanceSquared(points[i], points[j]), i, j });
    }
  }
  pairs.sort((a, b) => a.dist - b.dist);

  const uf = createUnionFind(n);

  for (const { i, j } of pairs.slice(0, connections)) {
    uf.union(i, j);
  }

  const sizes = uf.getCircuitSizes();
  return sizes[0] * sizes[1] * sizes[2];
};

console.log(solve(input));

