class UnionFind {
  private parent: Int32Array;
  private rank: Int32Array;
  private readonly n: number;

  constructor(n: number) {
    this.n = n;
    this.parent = new Int32Array(n).map((_, i) => i);
    this.rank = new Int32Array(n);
  }

  private get(arr: Int32Array, x: number): number {
    return arr[x] ?? 0;
  }

  find(x: number): number {
    const p = this.get(this.parent, x);
    if (p !== x) {
      this.parent[x] = this.find(p);
      return this.get(this.parent, x);
    }
    return p;
  }

  union(x: number, y: number): void {
    const px = this.find(x);
    const py = this.find(y);
    if (px === py) return;
    const rankPx = this.get(this.rank, px);
    const rankPy = this.get(this.rank, py);
    if (rankPx < rankPy) {
      this.parent[px] = py;
    } else if (rankPx > rankPy) {
      this.parent[py] = px;
    } else {
      this.parent[py] = px;
      this.rank[px]++;
    }
  }

  getCircuitSizes(): number[] {
    const sizes = new Map<number, number>();
    for (let i = 0; i < this.n; i++) {
      const root = this.find(i);
      sizes.set(root, (sizes.get(root) ?? 0) + 1);
    }
    return [...sizes.values()].sort((a, b) => b - a);
  }
