const input = await Bun.file(import.meta.dir + "/data/input.txt").text();

const parseGraph = (input: string): Map<string, string[]> => {
  const graph = new Map<string, string[]>();
  for (const line of lines(input)) {
    const [node, targets] = line.split(": ");
    graph.set(node, targets.split(" "));
  }
  return graph;
};

const countPaths = (
  graph: Map<string, string[]>,
  from: string,
  to: string,
  memo: Map<string, number> = new Map(),
): number => {
  if (from === to) return 1;
  if (memo.has(from)) return memo.get(from)!;

  const neighbors = graph.get(from) ?? [];
  let totalPaths = 0;
  for (const neighbor of neighbors) {
    totalPaths += countPaths(graph, neighbor, to, memo);
  }

  memo.set(from, totalPaths);
  return totalPaths;
};

export const solve = (input: string) => {
  const graph = parseGraph(input);
  return countPaths(graph, "you", "out");
};

console.log(solve(input));

