import { lines } from "../../../lib/utils";

const input = await Bun.file(import.meta.dir + "/data/input.txt").text();

type Point = { x: number; y: number; z: number };

const parsePoints = (input: string): Point[] =>
  lines(input).map((line) => {
    const parts = line.split(",").map(Number);
    return { x: parts[0]!, y: parts[1]!, z: parts[2]! };
  });

const distanceSquared = (a: Point, b: Point): number =>
  (a.x - b.x) ** 2 + (a.y - b.y) ** 2 + (a.z - b.z) ** 2;

class UnionFind {
  parent: number[];
  rank: number[];
  size: number[];

  constructor(n: number) {
    this.parent = Array.from({ length: n }, (_, i) => i);
    this.rank = Array(n).fill(0);
    this.size = Array(n).fill(1);
  }

  find(x: number): number {
    if (this.parent[x] !== x) {
      this.parent[x] = this.find(this.parent[x]!);
    }
    return this.parent[x]!;
  }

  union(x: number, y: number): void {
    const px = this.find(x);
    const py = this.find(y);
    if (px === py) return;

    if (this.rank[px]! < this.rank[py]!) {
      this.parent[px] = py;
      this.size[py]! += this.size[px];
    } else if (this.rank[px]! > this.rank[py]!) {
      this.parent[py] = px;
      this.size[px]! += this.size[py];
    } else {
      this.parent[py] = px;
      this.size[px]! += this.size[py];
      this.rank[px]!++;
    }
  }

  getSize(x: number): number {
    return this.size[this.find(x)]!;
  }
}

export const solve = (input: string, connections = 1000) => {
  const points = parsePoints(input);
  const n = points.length;

  const pairs: { i: number; j: number; dist: number }[] = [];
  for (let i = 0; i < n; i++) {
    for (let j = i + 1; j < n; j++) {
      pairs.push({ i, j, dist: distanceSquared(points[i]!, points[j]!) });
    }
  }

  pairs.sort((a, b) => a.dist - b.dist);

  const uf = new UnionFind(n);
  const limit = Math.min(connections, pairs.length);
  for (let k = 0; k < limit; k++) {
    uf.union(pairs[k]!.i, pairs[k]!.j);
  }

  const circuitSizes = Array.from({ length: n }, (_, i) => i)
    .filter((i) => uf.find(i) === i)
    .map((i) => uf.getSize(i))
    .sort((a, b) => b - a);

  return circuitSizes[0]! * circuitSizes[1]! * circuitSizes[2]!;
};

console.log(solve(input));

